import { TextField, Button, Container, Typography, makeStyles } from '@material-ui/core'
import { ArrowBackOutlined, ArrowRightOutlined, Block, ClassRounded } from '@mui/icons-material'
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import React from 'react'
import { useState } from 'react'
const useStyles = makeStyles({
  btn: {
    fontSize: 60,
    backgroundColor: 'violet',
    '&:hover': {
      backgroundColor: 'blue'
    }
  },
  title: {
    textDecoration: 'underline',
    marginBottom: 20
  },
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block',
  }
}
)

export default function Create() {
  const classes = useStyles();
  const [title, setTitle] = useState('');
  const [details, setDetails] = useState('');
  const [titleError, setTitleError] = useState(false);
  const [detailsError, setDetailsError] = useState(false);
  const handleSubmit = (e) => {

    title == '' ? setTitleError(true) : setTitleError(false);
    details == '' ? setDetailsError(true) : setDetailsError(false);

    e.preventDefault()
    if (title && details) {

      console.log(title, details)
    }
  }
  return (
    <div>
      <Container>

        <Typography
          variant='h6'
          color='textSecondary'
          component='h2'
          gutterBottom
        >
          Create Note Here
        </Typography>
        <form noValidate autoComplete='off' onSubmit={handleSubmit}>
          <TextField
            onChange={(e) => { setTitle(e.target.value) }}
            label='Note Title'
            variant='outlined'
            color='secondary'
            fullWidth
            required
            className={classes.field}
            error={titleError}
          />
          <TextField
            onChange={(e) => { setDetails(e.target.value) }}
            label='Detail'
            variant='outlined'
            color='secondary'
            fullWidth
            required
            className={classes.field}
            multiline
            rows={5}
            error={detailsError}
          />
          <RadioGroup>
            <FormControlLabel control={<Radio/>} value='money' label='money' />
            <FormControlLabel control={<Radio/>} value='work' label='work' />
            <FormControlLabel control={<Radio/>} value='todos' label='money' />
          </RadioGroup>


          <Button
            variant='contained'
            color='secondary'
            endIcon={<ArrowRightOutlined />}
            type='submit'
          >

            SUBMIT
          </Button>

        </form>

      </Container>
    </div>
  )
}
